def sort_list(sublist, fps):
    list = sublist
    j=0
    masterlist = []
    for i in range(len(list)):

        #print(j)
        frame = list[i][0]
        sub = list[i][1]
    
        similarity = SequenceMatcher(None, sub, list[j][1]).ratio()

        if similarity <=0.60:
            time_tuple=(j/fps,frame/fps)
            sub_list = [list[j+k][1] for k in range(i-j)]
            masterlist.append((time_tuple, sub_list))
            j=frame
        elif i == len(list)-1:
            time_tuple=(j/fps,frame/fps)
            sub_list = [list[j+k][1] for k in range(i-j+1)]
            masterlist.append((time_tuple, sub_list))

    print(masterlist)
    return masterlist

