def find_best_sub(l):
    masterlist = []
    for i in range(len(l)):
        df = pd.Series(l[i][1]).astype(str)
        count = df.value_counts()
        sub = list(count.head().index)[0]
        new_tuple = (l[i][0],sub)
        masterlist.append(new_tuple)
    return masterlist

